import {useSelector, useDispatch} from 'react-redux';
import {useEffect} from 'react';
//import constants for action types
import './App.css';

function App() {
  const dispatch = useDispatch();
  const people = useSelector((state)=>state.peopleReducer);
  const {success} = people;

  useEffect(()=>{
    setTimeout(()=>{
      dispatch({type:'addPerson', payload:'Ann'});
    },5000);

  },[success]);

  useEffect(()=>{
    dispatch({type:'getPeople',payload:['Tim','Joe','Jimmy']});
  },[]);

  return (
    <div className="App">
      {people && people.success && <h1>Person Added</h1>}
    </div>
  );
}

export default App;
